SELECT (Select
       max(case when name='ControlName' then convert(NVarchar(50),StringValue) else '' end) as ControlName,
       max(case when name='ControlValue' then convert(NVarchar(50),StringValue) else '' end) as ControlValue,
       max(case when name='Default' then convert(bit,StringValue) else 0 end) as [Default]
From parseJSON
(contentJson)
where ValueType = 'string' OR ValueType = 'boolean'
group by parent_ID)
FROM WF_TASKDetail WHERE TASKHEADID = 'E75A3FCE-72C9-4292-9AF8-00055FD4C4F8'
